import Bar from "./Webbar";
import { useState } from "react";

let SearchAmbulance = () => {

    const [city, setCity] = useState([]);
    const[data, setData]  = useState([]);
    const[acity, setAcity] = useState("");

    let GetCities = () => {
            fetch("http://localhost:8080/getambulancecities")
            .then(res => res.json())
            .then(usercity => setCity(usercity));
            return city;
    }

    let ambulancedata = (acity)=>{
        fetch("http://localhost:8080/getAmbulances?city="+acity)
        .then(res=>res.json())
        .then(ambdata => setData(ambdata))
        .then(console.log(data))
    }


    return (
        <>
            <Bar/>
            <div className="container mt-5 ">

                <select className="btn btn-light border border-dark dropdown-toggle" onClick={GetCities} >
                    <option>Select City</option>
                    {
                        city.map((item) => {
                            return (

                                <option onChange={ddlHandler(e=>setAcity(e.city))} key={item.city}><a href="{item}">{item}</a></option>
                            )
                        })
                    }
                </select >
                <button className="btn btn-primary" onClick={ambulancedata(acity)}>Search</button>
            </div>

            <div>
                {
                    data.map((amb)=>{
                        return(
                            <table>
                                <tr>{amb.drivername}</tr>
                            </table>
                        )
                    })
                }
            </div>
        </>
    );
}
export default SearchAmbulance;
