import Bar from "./Webbar";
import { useEffect, useState } from "react";

let SearchAmbulance = () => {

    const [city, setCity] = useState([]);
    const[data, setData]  = useState([]);
    const[acity, setAcity] = useState("");

    // let GetCities = () => {
    //         fetch("http://localhost:8080/getambulancecities")
    //         .then(res => res.json())
    //         .then(usercity => setCity(usercity));
    //         return city;
    // }

    useEffect( ()=>
    {

        fetch("http://localhost:8080/getambulancecities")
        .then(res => res.json())
        .then(usercity => setCity(usercity));   //city
    },[] )


    let ambulancedata = (acity)=>{
        fetch("http://localhost:8080/getAmbulances?city="+acity)
        .then(res=>res.json())
        .then(ambdata => setData(ambdata))
        .then(console.log(data))
    }


    return (
        <>
            <Bar/>
            <div className="container mt-5 ">

                <select name="city" className="btn btn-light border border-dark dropdown-toggle" onClick={GetCities} >
                    <option >Select City</option>
                    {
                        city.map((item) => {
                            return (

                                // <option onChange={setAcity(item.city)} key={item.city}><a href="{item}">{item}</a></option>
                                <option onClick={setAcity(item)} key={item.city}>{item}</option>
                            )
                        })
                    }
                </select >
                <button className="btn btn-primary" onClick={ambulancedata(acity)}>Search</button>
            </div>

            <div>
                {
                    data.map((amb)=>{
                        return(
                            <table>
                                <tr>{amb.drivername}</tr>
                            </table>
                        )
                    })
                }
            </div>
        </>
    );
}



export default SearchAmbulance;
