import Bar from "./Webbar";
import { useEffect, useState } from "react";

let SearchAmbulance = () => {

    const [city, setCity] = useState([]);
    const [data, setData] = useState([]);
    const [acity, setAcity] = useState("");

    useEffect(() => {
        fetch("http://localhost:8080/getambulancecities")
            .then(res => res.json())
            .then(usercity => setCity(usercity));   //city
    }, [])




    let ambulancedata = ()=>{
        fetch("http://localhost:8080/getAmbulances?city="+acity)
        .then(res=>res.json())
        .then(ambdata => setData(ambdata))
        .then(console.log(data))
    }

    const handleChange = event => {
        console.log(event.target.value);
        acity
    };


    return (
        <>
            <Bar />
            <div className="container mt-5 ">

                <select onChange={handleChange} name="cname" className="btn btn-light border border-dark dropdown-toggle"  >
                    <option value='' >Select City</option>
                    {
                        city.map(() => {
                            return (

                                // <option onChange={setAcity(item.city)} key={item.city}><a href="{item}">{item}</a></option>
                                // <option onClick={setAcity(item)} key={item.city}>{item}</option>
                                <option>{city}</option>
                            )
                        })
                    }
                </select>
                <button className="btn btn-primary" onClick={ambulancedata}>Search</button>
                {/* <button className="btn btn-primary" >Search</button> */}
            </div>

            {/* <div>
                {
                    data.map((amb)=>{
                        return(
                            <table>
                                <tr>{amb.drivername}</tr>
                            </table>
                        )
                    })
                }
            </div> */}
        </>
    );
}



export default SearchAmbulance;
