const validateData = (name, value) => {
    let hasError = false, error = "";
    switch (name) {
        case "uname":
            let regex1 = /^[a-zA-Z0-9]([._-](?![._-])|[a-zA-Z0-9]){3,8}[a-zA-Z0-9]$/;
            if (!regex1.test(value)) {
                hasError = true;
                error = "User name A-Z a-z 0-9 ._- a-z len-10 "
            }
            break;

        case "password":
            let regex2 = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{6,}$/;
            if (!regex2.test(value)) {
                hasError = true;
                error = "len 6 one Upper & smaller char one number & special symbol "
            }
            break;

        case "drivername":
            let regex3 = /^[A-Z][a-z]{2,15}$/;
            if (!regex3.test(value)) {
                hasError = true;
                error = "len-15 Driver name - first letter capital, rest - small "
            }
            break;

        case "aregno":
            let regex4 = /^[A-Z][A-Z][0-9]{10}$/;
            if (!regex4.test(value)) {
                hasError = true;
                error = "Only capitals and number 10 length "
            }
            break;

        case "avehicalno":
            let regex5 = /^[A-Z]{2}[ -][0-9]{1,2}(?: [A-Z])?(?: [A-Z]*)? [0-9]{4}$/;
            if (!regex5.test(value)) {
                hasError = true;
                error = "Enter Corect RTO Number "
            }
            break;

        case "mono":
            let regex6 = /^(\+\d{1,3}[- ]?)?\d{10}$/;
            if (!regex6.test(value)) {
                hasError = true;
                error = "10 digit mo no "
            }
            break;

        case "pincode":
            let regex7 = /^[0-9]{6}$/;
            if (!regex7.test(value)) {
                hasError = true;
                error = "len 6 digit  "
            }
            break;


    }
    return { hasError, error }

}

