import Bar from "./Webbar";
import { useState } from "react";

let SearchHospital = () => {

    const [city, setCity] = useState([]);

    let GetCities = () => {
            fetch("http://localhost:8080/getcities")
            .then(res => res.json())
            .then(usercity => setCity(usercity));
            return city;
    }

    return (
        <>
            <Bar/>
            <div className="container mt-5 ">

                <select className="btn btn-light border border-dark dropdown-toggle" onClick={GetCities}>
                    <option>Select City</option>
                    {
                        city.map((item) => {
                            return (
                                <option key={item.city}>{item}</option>
                            )
                        })
                    }
                </select>
                <button className="btn btn-primary">Search</button>
                
            </div>
        </>
    );
}
export default SearchHospital;
